frappe.ui.form.on('Quotation Item', "item_code", function(frm, cdt, cdn) {
    // locals[cdt][cdn]['product']

    var ItemGroup = 0.00
    var Item = 0.00
    var Customer = 0.00
    var TotalCost = 0.00
    var RMC = 0.00

    var Totalper = 0.00
    var TotalValue = 0.00


    // getting percentage item
    frappe.call({
        method: 'frappe.client.get_value',
        args: {
            'doctype': 'Item',
            'filters': {
                'name': locals[cdt][cdn]['item_code']
            },
            'fieldname': [
                'percentage',
            ]
        },
        async: false,
        callback: function(r) {
            if (!r.exc) {
                // code snippet
                if (r.message.percentage) {
                    Item = r.message.percentage

                }
            }
        }
    });

    // getting percentage item item_group
    frappe.call({
        method: 'frappe.client.get_value',
        args: {
            'doctype': 'Item Group',
            'filters': {
                'name': locals[cdt][cdn]['item_group']
            },
            'fieldname': [
                'percentage',
            ]
        },
        async: false,
        callback: function(r) {
            if (!r.exc) {
                // code snippet
                if (r.message.percentage) {
                    ItemGroup = r.message.percentage

                }
            }
        }
    });

    // getting percentage item Customer
    frappe.call({
        method: 'frappe.client.get_value',
        args: {
            'doctype': 'Customer',
            'filters': {
                'name': frm.doc.party_name
            },
            'fieldname': [
                'percentage',
            ]
        },
        async: false,
        callback: function(r) {
            if (!r.exc) {
                // code snippet
                if (r.message.percentage) {
                    Customer = r.message.percentage

                }
            }
        }
    });

    // getting percentage item Bom
    frappe.call({
        method: 'frappe.client.get_value',
        args: {
            'doctype': 'BOM',
            'filters': {
                'item': locals[cdt][cdn]['item_code'],
                "is_default": 1
            },
            'fieldname': [
                'raw_material_cost',
                'total_cost',
                "quantity"
            ]
        },
        async: false,
        callback: function(r) {
            if (!r.exc) {
                // code snippet
                if (r.message.raw_material_cost) {
                    RMC = r.message.raw_material_cost / r.message.quantity

                }
                if (r.message.total_cost) {
                    TotalCost = r.message.total_cost / r.message.quantity

                } else {
                    frappe.call({
                        method: 'frappe.client.get_value',
                        args: {
                            'doctype': 'Item',
                            'filters': {
                                'name': locals[cdt][cdn]['item_code']
                            },
                            'fieldname': [
                                'trading_item_flp',
                                'po_amount'
                            ]
                        },
                        async: false,
                        callback: function(r) {
                            if (!r.exc) {
                                // code snippet
                                if (r.message.trading_item_flp) {
                                    TotalCost = r.message.trading_item_flp
                                    RMC = r.message.po_amount

                                }
                            }
                        }
                    });


                }

            }
        }
    });

    if (isNaN(ItemGroup) == false && isNaN(Item) == false && isNaN(Customer) == false && isNaN(TotalCost) == false && isNaN(RMC) == false) {
        Totalper = ItemGroup + Item + Customer

        TotalValue = (Totalper * TotalCost) / 100

        locals[cdt][cdn]['rmc_'] = RMC.toFixed(2);
        locals[cdt][cdn]['flp_'] = TotalCost.toFixed(2);
        locals[cdt][cdn]['ufp_'] = (TotalCost + TotalValue).toFixed(2);



    } else {


    }




    console.log(`______________`)
    // console.log(ItemGroup)
    console.log(`ItemGroup : ${ItemGroup}  `)
    console.log(`Item : ${Item}  `)
    console.log(`Customer : ${Customer}  `)
    console.log(`TotalCost : ${TotalCost}  `)
    console.log(`RMC : ${RMC}  `)
    console.log(`Totalper : ${Totalper}  `)
    console.log(`TotalValue : ${TotalValue}  `)


})

frappe.ui.form.on('Purchase Order', {

	on_submit(frm) {
		// your code here
		let a =frm.doc.items 
        for (let i in a){
            const iterator = a[i];
            // console.log(iterator.item_code)


            let processing_amount = 0
            frappe.call({
                    method: 'frappe.client.get_value',
                    args: {
                        'doctype': 'Item',
                        'filters': {'name': iterator.item_code},
                        'fieldname': ['processing_amount']
                     },
                    async: false,
                    callback: function(r) {
                        if (!r.exc) {
                            // code snippet
                            processing_amount = r.message.processing_amount
                            console.log(r.message.processing_amount)

                        }
                    }
                });

            // console.log(po_amount)
            // console.log(processing_amount)

            frappe.db.set_value('Item',iterator.item_code, {
                    po_amount: iterator.rate,
                    trading_item_flp :iterator.rate + processing_amount
                }).then(r => {
                    let doc = r.message;
                    console.log(doc);
                })


        }



	}


})

